
public class Percentagens {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double Tx=0.456789*100;
		System.out.printf("%-6.3f%c\n", Tx, '%');
		System.out.printf("%-5.2f%c\n", Tx, '%');
		System.out.printf("%-4.1f%c\n", Tx, '%');

	}

}
